/**
 * A class
 */
class C {

    /**
     * A property
     *
     * @var int|null
     */
    public ?int $prop = 0;

    /**
     * A function
     *
     * @param int $p
     * @return string
     */
    public function f(int $p): string {
        return strval($p);
    }

}


